.function-menu-wrap {
    position: absolute;
    top: 0px;
    left: 265px;
    zoom: 1;
    /*å¯¼èªèåæ¾ç¤ºæ´å¤*/
}

.function-menu-wrap .function-menu {
    float: left;
}

.function-menu-wrap .func-more {
    display: none;
    padding: 0 26px 0px 14px;
}

.function-menu-wrap .func-more .more-arrow {
    position: absolute;
    right: 10px;
    top: 28px;
    display: block;
    width: 8px;
    height: 5px;
    background-position: -4px -6px;
    background-repeat: no-repeat;
    background-image: url("/Frame/_content/img/bg-index.png");
    _background-image: url("/Frame/_content/img/bg-index.gif");
}

.function-menu-wrap .group-active .more-arrow {
    background-position-x: -21px;
}

.function-menu-wrap .func-home a {
    border-left-color: #0a82de;
    width: 60px;
}

.function-menu-wrap .func-group-wrap {
    float: left;
    position: relative;
    margin-right: 0px;
    display: inline;
}

.function-menu-wrap .func-group-wrap .more-items-arrow {
    display: none;
}

.function-menu-wrap .func-group-wrap .group-active {
    display: inline-block;
    position: relative;
    left: 1px;
    /*position: absolute;
                        left: 0px;
                        top: 0px;*/
    height: 60px;
    line-height: 60px;
    margin-top: 0;
    border: 1px solid #8bb6da;
    border-bottom: none;
    background-color: #ffffff;
    color: #0a82de;
    text-decoration: none;
    z-index: 30;
}

.function-menu-wrap .more-group-wrap {
    position: absolute;
    display: block;
    right: -1px;
    height: 40px;
    float: none;
}

.function-menu-wrap .more-group-wrap .func-group {
    height: 40px;
    width: 108px;
    margin: 0;
    color: #555555;
    border: 1px solid #8bb6da;
    border-top-width: 0;
    border-bottom-width: 0;
    background-color: #ffffff;
    text-align: left;
    line-height: 40px;
    font-weight: normal;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

.function-menu-wrap .more-group-wrap.more-group-first .func-group {
    border-top-width: 1px;
}

.function-menu-wrap .more-group-wrap.more-group-last .func-group {
    border-bottom-width: 1px;
}

.function-menu-wrap .more-group-wrap .more-active {
    font-weight: bold;
    color: #0a82de;
    text-decoration: none;
}

.function-menu-wrap .more-group-wrap .func-items-wrap {
    left: auto;
    right: 135px;
    top: 0px;
    width: 160px;
    padding: 0px 1px;
    border-top-width: 1px;
}

.function-menu-wrap .more-group-wrap .func-items-wrap li {
    float: none;
    display: block;
    padding: 0;
}

.function-menu-wrap .more-group-wrap .func-items-wrap li a {
    position: relative;
    display: block;
    width: auto;
    height: 40px;
    padding: 0 0 0 42px;
    text-align: left;
    font-size: 13px;
    line-height: 40px;
    color: #555555;
}

.function-menu-wrap .more-group-wrap .func-items-wrap li .more-hover {
    color: #0a82de;
    background-color: #dfe9f3;
}

.function-menu-wrap .more-group-wrap .func-items-wrap li span {
    overflow: hidden;
    text-overflow: ellipsis;
    display: block;
    white-space: nowrap;
}

.function-menu-wrap .more-group-wrap .func-items-wrap li .icon,
.function-menu-wrap .more-group-wrap .func-items-wrap li .img-icon {
    position: absolute;
    left: 18px;
    top: 13px;
    width: auto;
    height: auto;
    margin: 0;
    padding: 0;
    font-size: 16px;
    line-height: 16px;
}

.function-menu-wrap .more-group-wrap .func-items-wrap li .img-icon {
    width: 16px;
    height: 16px;
}

.function-menu-wrap .more-group-wrap .more-items-arrow {
    left: 0px;
    top: 14px;
    position: absolute;
    display: block;
    visibility: hidden;
    width: 8px;
    height: 14px;
    background-position: -143px -1px;
    background-repeat: no-repeat;
