    color: #0a82de;
}

.func-items-wrap li .func-hover .icon {
    color: #fff;
    background: url("/Frame/_content/img/func-hover.png") no-repeat;
}

.func-items-wrap li .icon {
    display: block;
    height: 60px;
    width: 60px;
    font-size: 38px;
    line-height: 60px;
    text-align: center;
    color: #0a82de;
    margin: 0 auto;
    background: none;
}

.func-items-wrap li .img-icon {
    text-align: center;
    display: block;
    padding: 10px 21px;
    width: 38px;
    height: 38px;
    margin: 0 auto;
    border: none;
}

.func-items-wrap li .disabled {
    color: #d9e2e9;
    cursor: default;
}

.func-items-wrap li .disabled:hover {
    color: #d9e2e9;
}

.func-items-wrap li .disabled .img-icon {
    filter: alpha(opacity=30);
}

.func-items-wrap li .disabled .icon {
    color: #d9e2e9;
    cursor: default;
    background: none;
}

