function getParam(s) {
    var r = /helpkey=[\w]+/ig;
    if (r.test(s)) {
        return "?" + s.match(r);
    }
    else {
        r = /funcid=[\d]+|mode=[\d]+/ig;
        if (r.test(s)) {
            var ary = s.match(r);
            var p = "?";
            for (var i = 0; i < ary.length; i++) {
                if (i == ary.length - 1) {
                    p += ary[i];
                }
                else {
                    p += ary[i] + "*";
                }
            }
            return p;
        }
        else {
            return "";
        }
    }
}
function getAppName() {
