function esbShowDialog(pageCode, sParams, iWidth, iHeight, callbackname, oArgs, isOpen) {
    var showM = [{ esbcallback: "1"}];
    var url = esbGetUrl(pageCode, showM);

    if (!iWidth) iWidth = 500;
    if (!iHeight) iHeight = 340;
    var winSetting = "";
    if (isOpen) {
        var iW, iH, iL, iT;
        iH = iHeight > 60 ? (iHeight - 60) : 60;
        iW = iWidth > 10 ? (iWidth - 10) : 10;
        iL = (window.screen.width - iW) / 2;
        iT = (window.screen.height - iH - 80) / 2;

        if (iL < 0) iL = 0;
        if (iT < 0) iT = 0;

        winSetting = "left=" + iL + "px, top=" + iT + "px, height=" + iH + "px, width=" + iW + "px, status=1,resizable=1";
    }
    else
        winSetting = "dialogWidth:" + iWidth + "px;dialogHeight:" + iHeight + "px; status:0; help:0; resizable:0; center:1;";

    var param = {};
    param.fromurl = location.protocol + "//" + location.host;
    param.isOpen = isOpen;
    param.sParams = sParams;
    param.oArgs = oArgs;
    param.callback = callbackname;

    var methodArgs = [];
    methodArgs.push("");
    methodArgs.push(param);
    methodArgs.push(winSetting);

    var frame = document.createElement("iframe");
    frame.style.width = 0;
    frame.style.height = 0;
    frame.style.display = "none";
    document.body.appendChild(frame);
    frame.contentWindow.name = toJSON(methodArgs);
    frame.contentWindow.location = url;
}

var getType = function (o) {
    var _toS = Object.prototype.toString;
    var _types = {
        'undefined': 'undefined',
        'number': 'number',
        'boolean': 'boolean',
        'string': 'string',
        //'[object Function]': 'function',
        '[object RegExp]': 'regexp',
        '[object Array]': 'array',
        '[object Date]': 'date',
        '[object Error]': 'error'
    };
    return _types[typeof o] || _types[_toS.call(o)] || (o ? 'object' : 'null');
};
var specialChars = { '\b': '\\b', '\t': '\\t', '\n': '\\n', '\f': '\\f', '\r': '\\r', '"': '\\"', '\\': '\\\\' };
var replaceChars = function (chr) {
    return specialChars[chr] || '\\u00' + Math.floor(chr.charCodeAt() / 16).toString(16) + (chr.charCodeAt() % 16).toString(16);
};
var toJSON = function (o) {
    var s = [];
    switch (getType(o)) {
        case 'undefined':
            return 'undefined';
            break;
        case 'null':
            return 'null';
            break;
        case 'number':
        case 'boolean':
        case 'date':
        case 'function':
            return o.toString();
            break;
        case 'string':
            return '"' + o.replace(/[\x00-\x1f\\"]/g, replaceChars) + '"';
            break;
        case 'array':
            for (var i = 0, l = o.length; i < l; i++) {
                s.push(toJSON(o[i]));
            }
            return '[' + s.join(',') + ']';
            break;
        case 'error':
        case 'object':
            for (var p in o) {
                s.push(p + ':' + toJSON(o[p]));
            }
            return '{' + s.join(',') + '}';
            break;
        default:
            return '';
            break;
    }
};
var evalJSON = function (s) {
    if (getType(s) != 'string' || !s.length) return null;
    return eval('(' + s + ')');
};

