function getQueryString(sURL, sParamName) {
    if (Trim(sURL) == "")
        return undefined;
    var arrParam;
    var sParamValue;
    arrParam = decodeURIComponent(sURL.substring(sURL.indexOf("?") + 1)).split("&");
    for (var i = 0; i < arrParam.length; i++) {
        if (arrParam[i].indexOf(sParamName + "=") != -1) {
            var reg = new RegExp(sParamName + "=", "g");
            var s = arrParam[i].replace(reg, "");
            s = s.replace(/%2f/ig, "/");
            return s;
        }
    }
    return undefined;
}
