function StringToFloat(str) {
    if (typeof (str) == "string") {
        var val;
        val = Trim(str).replace(/,/g, "");
        if (val == "")
            return 0;
        else {
            if (isNaN(val)) {
                return str;
            }
            else {
                return parseFloat(val);
            }
        }
    }
    else {
        return str;
    }
}
