                        queryString += key.toString() + "=" + escape(obj[key].toString()) + "&";
                    }
                }
                url = url + (url.indexOf("?") > 0 ? "&" : "?") + queryString;
            }
            if (isOpen) {
                window.open(url, "_blank", winSetting);
            } else {
                var resultValue = window.showModalDialog(url, oArgs, winSetting);
                eval("window." + callbackname + "(\"" + toJSON(resultValue).replace(/"/g, "\\\"") + "\");");
            }
            return;
        }

        var param = {};
        param.fromurl = location.protocol + "//" + location.host;
        param.isOpen = isOpen;
        param.sParams = sParams;
        param.oArgs = oArgs;
        param.callback = callbackname;

        var methodArgs = [];
        methodArgs.push("");
        methodArgs.push(param);
        methodArgs.push(winSetting);

        var frame = document.createElement("iframe");
        frame.style.width = 0;
        frame.style.height = 0;
        frame.style.display = "none";
        document.body.appendChild(frame);
        frame.contentWindow.name = toJSON(methodArgs);
        frame.contentWindow.location = url;
    }
})();

