function isNumber(name) //æ°å¼æ£æµ
{
	if(name.length == 0)
		return false;
	
	for(i = 0; i < name.length; i++) 
	{
		if(name.charAt(i) < "0" || name.charAt(i) > "9")
		return false;
	}
		return true;
}
function isFloat(tmpFloatValue)
{
	var tmpLength = tmpFloatValue.length;
	var count = 0;
		
	for (var i = 0; i < tmpLength; i++)
	{
		aChar = tmpFloatValue.substring(i,i+1);
		if (aChar != "." && !isNumber(aChar))
		{
			return false;
		}    
	}
	for (var i = 0; i < tmpLength; i++)
	{
		aChar = tmpFloatValue.substring(i,i+1);
		count = 0;
		if (aChar == ".")
		{
			count++;
		}    
	}
	if (count > 1)
	{
		return false;
	}
	return true;
}
function verifyDate(tmpDateValue)
{
	var tmpLength = tmpDateValue.length;
	if (tmpLength == 0)
	{
		return true; 
	}
	for (var i=0; i<tmpLength;i++)
	{
		aChar = tmpDateValue.substring(i,i+1);
		if(aChar != "-" && (aChar < "0" || aChar > "9")) 
		{
			return false;
		}
	}
	if ((tmpLength < 8 || tmpLength > 10) && tmpLength != 0) 
	{
		return false;  
	}
	for (var j= 0; j < 4;j++)
	{
		aChar = tmpDateValue.substring(j,j+1);
