function openXMLHTTP(sFile,sType,sSend)
{
	var rdNum = Math.random();
	var oHTTP = new ActiveXObject("Msxml2.XMLHTTP");
	
	var strTmp ="";
	if (sFile.indexOf("?") == -1)
	{
		strTmp ="?";
	}
	else
	{
		strTmp ="&";
	}
	
	var sUrl = sFile + strTmp +  "ywtype=" + myEscape(sType) + "&rdnum=" + rdNum; 
	
	if (sSend != "" && sSend != undefined)
	{
		oHTTP.open( "POST", sUrl, false); 
		oHTTP.send(sSend); 
	}
	else
	{
		oHTTP.open( "GET", sUrl, false); 
		oHTTP.send(); 
	}
	
	var bSuccess = handleXMLErr(oHTTP.responseXML); 
	if (bSuccess) 
	{
		return oHTTP.responseText;	
	}
	else
	{
		return "";
	}
}
function fnRequest(strUrl,strName) {
    ///	<summary>
    ///		è·åUrlåè·çåæ°å¼ï¼ç¸å½äºæå¡ç«¯çRequest.QueryString()æ¹æ³
    ///	</summary>
    ///	<returns type="String" />
    var strHref = strUrl;
    if (!strUrl) {
        strHref = window.document.location.href;
    }
    
    var intPos = strHref.indexOf("?");
    var strRight = strHref.substr(intPos + 1);
    var arrTmp = strRight.split("&");
    for (var i = 0; i < arrTmp.length; i++) {
        var arrTemp = arrTmp[i].split("=");
        if (arrTemp[0].toUpperCase() == strName.toUpperCase()) return arrTemp[1];
    }
    return "";
}
